import React from 'react';
import {baseurl} from '../shared/baseurl';

function Home(props) {
    if(props.home.isLoading)
    {
        return(
            <div className="container">
                LOADING...
            </div>
        );
    }
    else if(props.home.errmsg) {
        return(
            <div className="container">
                <div className="row">
                    <h4>{props.home.errmsg}</h4>
                </div>
            </div>
        );
    } 
    else if(props.home.home)   {
        const h = props.home.home.map((h) => {
            return(
                <div key={h.id} className="col-6 mt-1">
                   <a href={baseurl + h.url} class="card homeCard">
                        <img className="card-img-top" src={baseurl + h.image}/>
                        <div class="card-body">
                            <h5 class="card-title">{h.page}</h5>
                            <p className="card-text homeCardText">{h.description}</p>
                        </div>
                    </a>
                    
                </div>
            )
        })
        return(
            <div className="container">
                <div className="row">
                    {h}
                </div>
            </div>
        );
    }
    else {
        return(
            <div></div>
        );
    }
    
}

export default Home;
