import React from 'react';

class Contact extends React.Component {
    constructor(props){
        super(props);
      
    }
   
    render(){
        alert(this.props.contact.isLoading);
       if(this.props.contact.isLoading) {
           return(
               <div>LOADING...</div>
           );
       }
       if(this.props.contact.errmess) {
        return(
        <div>{this.props.contact.errmess}</div>
        );
    }
       else if(this.props.contact){
           const contacts = this.props.contact.map(con => {
               <div>con.name</div>
           });
            return(
                <div>
                    <img width="100%" src="assets/contactus.jpg" alt="ContactUS"/>
                    <div className="container">
                        <div className="row mt-2">
                            <h3> Contact US</h3>
                        </div>
                        <div className="row">
                            <p>Are you curious to know more about preschool? Feel free to contact us. </p>  
                            <p>Are you curious about our preschool and want to know more? Please do not hesitate to contact us.</p>                        
                        </div>
                    </div>
                    {contacts}
                </div>
            );
       }
       else{
           return(
               <div></div>
           );
       }
        
    }
}

export default Contact;
