import React from 'react';
import {Breadcrumb, BreadcrumbItem} from 'reactstrap';
import {Link} from 'react-router-dom';

function Work(props) {

    if(props.work.isLoading){
        return(
            <div className="container">
                <div className="row">
                    LOADING....
                </div>
            </div>
        );
    }
    else if(props.work.errmess){
        return(
            <div className="container">
                <div className="row">
                    {props.errmess}
                </div>
            </div>
        );
    }
    else if(props.work.work){
        const workImg = props.work.work.map((w) => {
            return(
                <div class="carousel-item active">
                    <img class="d-block w-100" src={baseurl + w.image} alt="First slide" height="250px" width="500px"/>
                </div>             
            );
        })

        const work = () => {
            return(
                <div id="carouselExampleControls" class="carousel slide" data-ride="carousel">
                    <div class="carousel-inner">

                    </div>
                    <a class="carousel-control-prev" href="#carouselExampleControls" role="button" data-slide="prev">
                        <span class="carousel-control-prev-icon" aria-hidden="true"></span>
                        <span class="sr-only">Previous</span>
                    </a>
                    <a class="carousel-control-next" href="#carouselExampleControls" role="button" data-slide="next">
                        <span class="carousel-control-next-icon" aria-hidden="true"></span>
                        <span class="sr-only">Next</span>
                    </a>
                </div>
            );
        }
        return(
            <div className="container">
                <div className="row mt-2">
                    <Breadcrumb>
                        <BreadcrumbItem><Link to="/home">Home</Link></BreadcrumbItem>
                        <BreadcrumbItem active>That's How We Work</BreadcrumbItem>
                    </Breadcrumb>       
                </div>
                <div className="row">
                    <h1 className="siteTextColor">That is how we work</h1>
                </div>
                <div className="row">
                    {work}
                </div>
                
            </div>
        );
    }
    else {
        return(
            <div></div>
        );
    }
}

export default Work;
