import React from 'react';
import {baseurl} from '../shared/baseurl';

function Home(props) {
    if(props.home.isLoading)
    {
        return(
            <div className="container">
                LOADING...
            </div>
        );
    }
    else if(props.home.errmsg) {
        return(
            <div className="container">
                <div className="row">
                    <h4>{props.home.errmsg}</h4>
                </div>
            </div>
        );
    } 
    else if(props.home.home)   {
        const h = props.home.home.map((h) => {
            return(
                <div key={h.id} className="col-6 col-sm-12">
                   <a href={baseurl + h.url}>
                        <img className="box-image" src={baseurl + h.image}/>
                    </a>
                </div>
            )
        })
        return(
            <div className="container">
                {h}
            </div>
        );
    }
    else {
        return(
            <div></div>
        );
    }
    
}

export default Home;
